def main():
    """Main function"""

    module = HwcModule(
        argument_spec=dict(
            state=dict(default='present', choices=['present', 'absent'], type='str'),
            name=dict(type='str'),
            availability_zone=dict(type='str'),
            source_volume_id=dict(type='str'),
            snapshot_id=dict(type='str'),
            desc=dict(type='str'),
            volume_type=dict(type='str'),
            size=dict(required=True, type='int'),
            metadata=dict(type='dict'),
            consistency_group_id=dict(type='str'),
            image_id=dict(type='str'),
            replica_volume_id=dict(type='str')
        )
    )
    session = HwcSession(module, 'storage')

    state = module.params['state']

    if not module.params.get("id"):
        module.params['id'] = get_resource_id(session)

    fetch = get_resource(session)
    changed = False

    if fetch:
        if state == 'present':
            expect = _get_resource_editable_properties(module)
            current_state = response_to_hash(module, fetch)
            if is_different(expect, current_state):
                fetch = update(session, self_link(session), [200])
                fetch = response_to_hash(module, fetch)
                changed = True
            else:
                fetch = current_state
        else:
            delete(session, self_link(session))
            fetch = {}
            changed = True
    else:
        if state == 'present':
            fetch = create(session, collection(session))
            fetch = response_to_hash(module, fetch)
            changed = True
        else:
            fetch = {}

    fetch.update({'changed': changed})

    module.exit_json(**fetch)


def create(session, link, success_codes=None):
    if not success_codes:
        success_codes = [201, 202]
    module = session.module
    r = return_if_object(module, session.post(link, resource_to_create(module)), success_codes)

    complete_states = ['available']
    allowed_states = ['downloading', 'creating', 'available']
    timeout = 60 * int(module.params['timeouts']['create'].rstrip('m'))
    r = wait_for_operation(session, r, timeout, complete_states, allowed_states)
    return r.get('volume')


