@link_wrapper
def async_op_url(session, extra_data=None):
    url = "{endpoint}volumes/{op_id}"

    combined = session.module.params.copy()
    if extra_data:
        combined.update(extra_data)

    combined['endpoint'] = session.get_service_endpoint('volumev2')

